import React, { useEffect, useState } from "react";
import axios from "../services/api";

function Certificados() {
    const [certificados, setCertificados] = useState([]);

    useEffect(() => {
        axios.get("/certificados/")
            .then(response => setCertificados(response.data))
            .catch(error => console.error("Erro ao buscar certificados:", error));
    }, []);

    return (
        <div>
            <h1>Lista de Certificados</h1>
            <ul>
                {certificados.map(cert => (
                    <li key={cert.id}>{cert.participante} - {cert.treinamento}</li>
                ))}
            </ul>
        </div>
    );
}

export default Certificados;
