import React, { useState, useEffect } from "react";
import { api } from "../services/api";
import jsPDF from "jspdf";
import "jspdf-autotable";

export default function ListarCertificados() {
  const [certificados, setCertificados] = useState([]);

  useEffect(() => {
    api.get("certificados/").then((res) => setCertificados(res.data));
  }, []);

  const excluirCertificado = async (id) => {
    await api.delete(`certificados/${id}/`);
    setCertificados(certificados.filter(certificado => certificado.id !== id));
    alert("Certificado excluído!");
  };

  const gerarPDF = (certificado) => {
    const doc = new jsPDF('landscape');

    // Adicionar imagem de cabeçalho
    const imgData = 'data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAA...'; // Substitua pelo valor base64 da sua imagem
    doc.addImage(imgData, 'PNG', 10, 10, 270, 50);

    // Adicionar título
    doc.setFontSize(30);
    doc.setTextColor(0, 0, 0);
    doc.text("CERTIFICADO", 148, 80, null, null, 'center');

    // Adicionar texto do certificado
    doc.setFontSize(16);
    doc.setTextColor(0, 0, 0);
    const text = `Certificamos que o Sr. ${certificado.participante_nome}, portador do RG: (número)
participou do Curso de Segurança em Trabalho em altura, em conformidade com a NR 35,
no período de (data) a (data) promovido pelo SESMT da Empresa (nome da empresa),
com carga horária de ${certificado.carga_horaria} horas.`;
    doc.text(text, 20, 100);

    // Adicionar local e data
    doc.text(`Cidade Brasileira ___ de __________ de ${new Date().getFullYear()}.`, 20, 140);

    // Adicionar assinaturas
    doc.text("______________________________", 20, 180);
    doc.text("Alexandre Timóteo", 20, 185);
    doc.text("Técnico em Segurança do Trabalho", 20, 190);

    doc.text("______________________________", 100, 180);
    doc.text("Raquel Guedes", 100, 185);
    doc.text("Concluinte", 100, 190);

    doc.text("______________________________", 180, 180);
    doc.text("Tomás Kanegi", 180, 185);
    doc.text("Concluinte", 180, 190);

    // Adicionar rodapé
    doc.text("Rua (nome) N° (nome) Quadra. (nome), Lotes (nome). Bairro: (nome)", 20, 210);

    doc.save(`certificado_${certificado.participante_nome}_${certificado.treinamento_titulo}.pdf`);
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">Lista de Certificados</h1>
      <table className="min-w-full bg-white">
        <thead>
          <tr>
            <th className="py-2">Participante</th>
            <th className="py-2">Treinamento</th>
            <th className="py-2">Carga Horária</th>
            <th className="py-2">Data de Emissão</th>
            <th className="py-2">Ações</th>
          </tr>
        </thead>
        <tbody>
          {certificados.map((certificado) => (
            <tr key={certificado.id}>
              <td className="border px-4 py-2">{certificado.participante_nome}</td>
              <td className="border px-4 py-2">{certificado.treinamento_titulo}</td>
              <td className="border px-4 py-2">{certificado.carga_horaria}</td>
              <td className="border px-4 py-2">{new Date(certificado.data_emissao).toLocaleDateString()}</td>
              <td className="border px-4 py-2">
                <button onClick={() => excluirCertificado(certificado.id)} className="bg-red-500 text-white px-4 py-2 rounded mr-2">
                  Excluir
                </button>
                <button onClick={() => gerarPDF(certificado)} className="bg-blue-500 text-white px-4 py-2 rounded">
                  Download PDF
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
