import React, { useState, useEffect } from "react";
import { api } from "../services/api";
import jsPDF from "jspdf";

export default function Certificados() {
  const [participantes, setParticipantes] = useState([]);
  const [treinamentos, setTreinamentos] = useState([]);
  const [participanteId, setParticipanteId] = useState("");
  const [treinamentoId, setTreinamentoId] = useState("");

  useEffect(() => {
    api.get("participantes/").then((res) => setParticipantes(res.data));
    api.get("treinamentos/").then((res) => setTreinamentos(res.data));
  }, []);

  const gerarCertificado = async () => {
    try {
      await api.post("certificados/", {
        participante: participanteId,
        treinamento: treinamentoId,
      });
      alert("Certificado gerado!");
    } catch (error) {
      console.error("Erro ao gerar certificado:", error.response ? error.response.data : error.message);
      alert(`Erro ao gerar certificado: ${error.response ? error.response.data : error.message}`);
    }
  };

  const gerarPDF = () => {
    const participante = participantes.find(p => p.id === participanteId);
    const treinamento = treinamentos.find(t => t.id === treinamentoId);

    if (!participante || !treinamento) {
      alert("Selecione um participante e um treinamento.");
      return;
    }

    const doc = new jsPDF();

    doc.text("Certificado de Treinamento", 20, 20);
    doc.text(`Certificamos que ${participante.nome}`, 20, 30);
    doc.text(`participou do treinamento ${treinamento.titulo}`, 20, 40);
    doc.text(`com carga horária de ${treinamento.carga_horaria} horas.`, 20, 50);
    doc.text(`Data: ${new Date().toLocaleDateString()}`, 20, 60);

    doc.save(`certificado_${participante.nome}_${treinamento.titulo}.pdf`);
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold">Gerar Certificado</h1>
      <select className="p-2 border mt-4" onChange={(e) => setParticipanteId(e.target.value)}>
        <option value="">Selecione um participante</option>
        {participantes.map((p) => (
          <option key={p.id} value={p.id}>{p.nome}</option>
        ))}
      </select>

      <select className="p-2 border mt-4" onChange={(e) => setTreinamentoId(e.target.value)}>
        <option value="">Selecione um treinamento</option>
        {treinamentos.map((t) => (
          <option key={t.id} value={t.id}>{t.titulo}</option>
        ))}
      </select>

      <button onClick={gerarCertificado} className="p-2 bg-green-500 text-white rounded mt-4">
        Gerar
      </button>
      <button onClick={gerarPDF} className="p-2 bg-blue-500 text-white rounded mt-4 ml-4">
        Download PDF
      </button>
    </div>
  );
}
